css:
atom-text-editor .find-result .region,
atom-text-editor::shadow .find-result .region {
  background-color: transparent;
  border-radius: 2px;
  border: 1px solid #888888;
  box-sizing: border-box;
  z-index: 0;
}
atom-text-editor .current-result .region,
atom-text-editor::shadow .current-result .region {
  border-radius: 2px;
  border: 1px solid #ffffff;
  box-sizing: border-box;
  z-index: 0;
}
atom-text-editor .find-result,
atom-text-editor::shadow .find-result,
atom-text-editor .current-result,
atom-text-editor::shadow .current-result {
  display: none;
}
atom-workspace.find-visible atom-text-editor .find-result,
atom-workspace.find-visible atom-text-editor::shadow .find-result,
atom-workspace.find-visible atom-text-editor .current-result,
atom-workspace.find-visible atom-text-editor::shadow .current-result {
  display: block;
}
.find-and-replace,
.project-find {
  -webkit-user-select: none;
  padding: 5px;
  overflow-x: auto;
}
.find-and-replace .header,
.project-find .header {
  padding: 2.5px 5px;
  min-width: 200px;
}
.find-and-replace .header-item,
.project-find .header-item {
  margin: 2.5px 0;
}
.find-and-replace .input-block,
.project-find .input-block {
  display: flex;
  flex-wrap: wrap;
  width: 100%;
  min-width: 200px;
}
.find-and-replace .input-block-item,
.project-find .input-block-item {
  display: flex;
  flex: 1;
  padding: 5px;
}
.find-and-replace .btn-group,
.project-find .btn-group {
  display: flex;
  flex: 1;
}
.find-and-replace .btn-group .btn,
.project-find .btn-group .btn {
  flex: 1;
}
.find-and-replace .btn-group + .btn-group,
.project-find .btn-group + .btn-group {
  margin-left: 10px;
}
.find-and-replace .btn-group-options .btn,
.project-find .btn-group-options .btn {
  line-height: 1;
}
.find-and-replace .btn > .icon,
.project-find .btn > .icon {
  width: 20px;
  height: 16px;
  vertical-align: middle;
  fill: currentColor;
  stroke: currentColor;
}
.find-and-replace .description,
.project-find .description {
  display: inline-block;
}
.find-and-replace .description .subtle-info-message,
.project-find .description .subtle-info-message {
  padding-left: 5px;
  color: #555555;
}
.find-and-replace .description .subtle-info-message .highlight,
.project-find .description .subtle-info-message .highlight {
  color: #aaaaaa;
  font-weight: normal;
}
.find-and-replace .options-label,
.project-find .options-label {
  color: #555555;
  position: relative;
}
.find-and-replace .options-label .options,
.project-find .options-label .options {
  color: #aaaaaa;
}
.find-and-replace .editor-container,
.project-find .editor-container {
  position: relative;
}
.find-and-replace .editor-container atom-text-editor,
.project-find .editor-container atom-text-editor {
  width: 100%;
}
.find-and-replace .input-block-item {
  flex: 1 1 260px;
}
.find-and-replace .input-block-item--flex {
  flex: 100 1 260px;
}
.find-and-replace .btn-group-find,
.find-and-replace .btn-group-replace {
  flex: 1 1 120px;
}
.find-and-replace .btn-group-replace-all,
.find-and-replace .btn-group-options {
  flex: 1 1 200px;
}
.find-and-replace .btn-group-options .btn {
  flex: 1 1 25%;
  padding: 0;
  text-align: center;
}
.find-and-replace .find-container atom-text-editor {
  padding-right: 64px;
}
.find-and-replace .find-meta-container {
  position: absolute;
  top: 1px;
  right: 0;
  margin: 5px 5px 0 0;
  z-index: 2;
  font-size: .9em;
  line-height: 25px;
  pointer-events: none;
}
.find-and-replace .find-meta-container .result-counter {
  margin-right: 10px;
}
.find-wrap-icon {
  display: none;
  position: absolute;
  top: 50% !important;
  left: 50% !important;
  right: initial !important;
  bottom: initial !important;
  margin-top: -55px;
  margin-left: -55px;
  background: rgba(19, 21, 22, 0.45);
  border-radius: 4px;
  text-align: center;
  pointer-events: none;
}
.find-wrap-icon:before {
  font-size: 64px;
  line-height: 110px;
  height: 110px;
  width: 110px;
  color: #c5c8c6;
  opacity: .5;
}
.project-find .input-block-item {
  flex: 1 1 220px;
}
.project-find .input-block-item--flex {
  flex: 100 1 260px;
}
.project-find .btn-group-find {
  flex: .6;
}
.project-find .loading,
.project-find .preview-block,
.project-find .error-messages,
.project-find .filter-container {
  display: none;
}
.preview-pane {
  position: relative;
  display: -webkit-flex;
  -webkit-flex-direction: column;
}
.preview-pane .panel-heading {
  -webkit-flex-shrink: 0;
}
.preview-pane .loading-spinner-tiny,
.preview-pane .loading-spinner-tiny + .inline-block {
  vertical-align: middle;
}
.preview-pane .no-results-overlay {
  visibility: hidden;
}
.preview-pane.no-results .no-results-overlay {
  visibility: visible;
}
.preview-pane .results-view {
  overflow: auto;
  position: relative;
  padding: 0 10px;
  -webkit-flex: 1;
}
.preview-pane .results-view .search-result {
  padding-top: 2px;
  padding-bottom: 2px;
}
.preview-pane .results-view .line-number {
  margin-right: 1ex;
  text-align: right;
  display: inline-block;
}
.preview-pane .results-view .selected .line-number {
  color: #ffffff;
}
.preview-pane .results-view .path-match-number {
  padding-left: 10px;
  color: #555555;
}
.preview-pane .results-view .preview {
  word-break: break-all;
  font-family: monospace;
}
.preview-pane .results-view .selected .highlight-info {
  box-shadow: inset 0 0 1px #ffffff;
}
.preview-pane .results-view .selected .highlight-error {
  box-shadow: inset 0 0 1px #ff4d4d;
}
.preview-pane .results-view .selected .highlight-success {
  box-shadow: inset 0 0 1px #72efa8;
}
.find-container atom-text-editor::shadow .regexp .escape,
.replace-container atom-text-editor::shadow .regexp .escape {
  color: #5293d8;
}
.find-container atom-text-editor::shadow .regexp .range,
.replace-container atom-text-editor::shadow .regexp .range,
.find-container atom-text-editor::shadow .regexp .character-class,
.replace-container atom-text-editor::shadow .regexp .character-class,
.find-container atom-text-editor::shadow .regexp .wildcard,
.replace-container atom-text-editor::shadow .regexp .wildcard {
  color: #2bda77;
}
.find-container atom-text-editor::shadow .regexp .wildcard,
.replace-container atom-text-editor::shadow .regexp .wildcard {
  font-weight: bold;
}
.find-container atom-text-editor::shadow .regexp .set,
.replace-container atom-text-editor::shadow .regexp .set {
  color: inherit;
}
.find-container atom-text-editor::shadow .regexp .keyword,
.replace-container atom-text-editor::shadow .regexp .keyword,
.find-container atom-text-editor::shadow .regexp .punctuation,
.replace-container atom-text-editor::shadow .regexp .punctuation {
  color: #cc0000;
  font-weight: normal;
}
.find-container atom-text-editor::shadow .regexp .replacement.variable,
.replace-container atom-text-editor::shadow .regexp .replacement.variable {
  color: #ff982d;
}
