css:
.styleguide {
  position: relative;
  overflow: auto;
}
.styleguide > section.bordered {
  padding: 20px;
}
.styleguide > section :last-child {
  margin-bottom: 0;
}
.styleguide > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  color: #aaaaaa;
}
.styleguide > section.collapsed > * {
  display: none;
}
.styleguide section .section-heading {
  cursor: pointer;
  font-weight: bold;
  font-size: 1.6em;
  color: #ffffff;
}
.styleguide section .section-heading:hover {
  color: #ffffff;
}
.styleguide h2 {
  font-size: 1.5em;
  line-height: 1.2;
  margin: 1.75em 0 0 0;
  color: #ffffff;
}
.styleguide p {
  font-size: 1.2em;
  margin: .5em 0 1em 0;
}
.styleguide section[data-name="styleguide"] {
  border-top: none;
}
.styleguide .popover-list {
  position: relative;
}
.styleguide .select-list atom-text-editor[mini],
.styleguide .popover-list atom-text-editor[mini] {
  height: 27px;
}
.styleguide .tooltip {
  position: relative;
  opacity: 1;
  display: inline-block;
  margin-right: 10px;
}
.styleguide .btn-collapse-all {
  position: absolute;
  right: 10px;
  top: 10px;
  z-index: 100;
}
.example {
  display: -webkit-flex;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  margin: 10px 0;
}
.example > * {
  width: 50%;
}
.example .example-rendered {
  position: relative;
  padding: 10px;
  background: #222426;
  overflow: hidden;
  border-radius: 2px 0 0 2px;
}
.example .example-rendered .ui-site-1,
.example .example-rendered .ui-site-2,
.example .example-rendered .ui-site-3,
.example .example-rendered .ui-site-4,
.example .example-rendered .ui-site-5 {
  height: 10px;
  width: 100px;
}
.example .example-code {
  position: relative;
  background-color: #1d1f21;
  border-left: 1px solid rgba(0, 0, 0, 0.5);
}
.example .example-code.show-example-space-pen .example-space-pen {
  display: block;
}
.example .example-code.show-example-html .example-html {
  display: block;
}
.example .example-code .btn-group {
  position: absolute;
  top: 10px;
  right: 10px;
  z-index: 10;
}
.styleguide [data-name="icons"] .example-rendered {
  display: flex;
  flex-wrap: wrap;
  align-content: flex-start;
}
.styleguide [data-name="icons"] .icon {
  flex-basis: 50%;
  padding: 10px 0 10px 40px;
  color: #555555;
}
.styleguide [data-name="icons"] .icon:before {
  position: absolute;
  margin-left: -32px;
  color: #ffffff;
  text-align: center;
}
.styleguide [data-name="icons"] .icon:hover {
  color: #ffffff;
}
.styleguide [data-name="icons"] .icon:hover:before {
  color: #ffffff;
  font-size: 32px;
  width: 32px;
  height: 32px;
  margin-top: -8px;
  margin-left: -40px;
}
.styleguide [data-name="icons"] .icon-logo-github:before {
  margin-left: -60px;
}
.styleguide [data-name="icons"] .icon-logo-github:hover:before {
  margin-left: -100px;
}
